mapper.select:
SELECT COUNT(*) 
FROM member_tbl 
WHERE member_id = #{id}

SELECT member_id
FROM member_tbl
WHERE member_email = #{member_email}

SELECT COUNT(*)
FROM member_key_tbl
WHERE member_id = #{member_id} AND member_key = #{member_key}

SELECT member_pwd
FROM member_key_tbl mk, member_tbl m
WHERE m.member_email = #{member_email}
	AND mk.member_key = #{member_key}

mapper.update:
UPDATE member_tbl
SET
	member_step = #{member_step}
WHERE member_id = #{member_id}

mapper.select:
SELECT member_step
FROM member_tbl
WHERE member_id = #{member_id} AND member_pwd = #{member_pwd}

SELECT COUNT(*)
FROM member_tbl
WHERE member_id = #{userId}
